import createjs from 'create-js-builder';

import loaders from './loaders';
import helpers from './helpers';
import plugins from './plugins';
import requests from './requests';

const preload = {
  LoadItem: createjs.LoadItem,
  LoadQueue: createjs.LoadQueue,
  Methods: createjs.Methods,
  Types: createjs.Types,
  buildDate: new Date(createjs.PreloadJS.buildDate),
  version: createjs.PreloadJS.version,
}.inherit(loaders, helpers, plugins, requests);

export default preload;
